mapper.select:
  	SELECT b.id AS id, b.nick_name AS nickName, b.head_img AS headImg, b.team_id AS teamId, b.sex AS sex, b.address AS address, b.birthday AS birthday, b.pwd AS pwd,b.user_account AS userAccount, b.mobile AS mobile, b.create_time AS createTime, b.last_update_time AS lastUpdateTime, b.stat AS stat
	FROM sst_group_user AS a, sst_user AS b
	WHERE a.user_id = b.id AND a.stat = 1 AND b.stat = 1 AND a.group_id = #{groupId}
